package day4;

public class Largest_of_threenumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20,b=80,c=9;
		if((a>b)&&(a>c))
		{
			System.out.println(a+"is greater");
		}
		else if((b>a)&&(b>c))
		{
			System.out.println(b+"is greater");
			
		}
		else
		{
			System.out.println( c+ "is greatest");
		}
			

	}

}
